import pygameclass Particle:    def __init__(self, screen, color: tuple, x: int, y: int, radius: int) -> None:        self.acceleration = pygame.math.Vector2()        self.velocity = pygame.math.Vector2()        self.position = pygame.math.Vector2(x, y)        self.mass = 1        self.screen = screen        self.radius = radius        self.color = color        self.friction = 0.99        self.locked = False    def apply_force(self, force: pygame.math.Vector2) -> None:        f = force        f /= self.mass        self.acceleration += f    def update(self) -> None:        if not self.locked:            self.velocity *= self.friction            self.velocity += self.acceleration            self.position += self.velocity            self.acceleration *= 0    def show(self) -> None:        pygame.draw.circle(self.screen, self.color, (self.position.x, self.position.y), self.radius)